package assignment3;

public class Evennumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 100;
		System.out.println("Even Numbers from 10 to "+n+" are: ");
		for (int i = 10; i <= n; i++) {
		   //if number%2 == 0 it means its an even number
		   
			if (i % 2 == 0) {
			System.out.print(i + " ");
	}

}}}
