package assignment3;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=101;
		
		switch(n%2){//this will return 0
		case 0:
		    System.out.println(n+" is an Even number");
		    break;
		case 1:
		    System.out.println(n+" is an Odd number");

	}
}}
